CREATE TABLE telenutrition.patient_fact_source (
    patient_fact_source_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    source_type VARCHAR(50) NOT NULL, -- EMR, platform, etc...
    source_code VARCHAR(50) NOT NULL, -- e.g., Epic, Cerner, telenutrition etc.
    reporting_type VARCHAR(20) NOT NULL DEFAULT 'self_reported',
    reported_by VARCHAR(50) NOT NULL, -- patient, dietian
    source_reference_id INT NOT NULL, -- could be an encounter_id, questionnaire_id, etc.
    description TEXT,
    UNIQUE (source_reference_id, source_code)
);

CREATE TABLE telenutrition.ontology_concept (     
    ontology_concept_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,     
    concept_code VARCHAR(100) UNIQUE NOT NULL,     
    concept_name VARCHAR(100) NOT NULL,
    concept_type VARCHAR(100), -- finding, disorder, etc.
    ontology_name VARCHAR(50),
    description TEXT
);

CREATE TABLE telenutrition.patient_attribute (
    patient_attribute_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    attribute_code VARCHAR(100) UNIQUE NOT NULL,
    attribute_description VARCHAR(20000),
    attribute_type VARCHAR(20) NOT NULL DEFAULT 'text', -- e.g., string, integer, date, boolean, list, clob, etc.
    validation_type VARCHAR(50),  -- The type of validation ('range', 'regex', 'set', etc.)
	validation_value TEXT,        -- The value for the validation (e.g., '0,120' for range or a regex pattern)
	error_message TEXT,         -- The error message if validation fails
    ontology_concept_id INT REFERENCES telenutrition.ontology_concept(ontology_concept_id) ON DELETE SET NULL,
    unit_name VARCHAR(100), -- e.g., centimeter
    unit_symbol VARCHAR(50) -- e.g., cm
);


CREATE TABLE telenutrition.patient_fact (
    patient_fact_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    patient_id INT NOT NULL,
    patient_attribute_id INT NOT NULL,
    ontology_concept_id INT NOT NULL,
    attribute_value TEXT, -- will either be a direct value in the system or be a co
    patient_fact_source_id INT NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    collected_at TIMESTAMP WITH TIME ZONE,
    is_active BOOLEAN DEFAULT TRUE,
    UNIQUE (patient_id, patient_attribute_id, created_at, collected_at),
    FOREIGN KEY (patient_id) REFERENCES telenutrition.schedule_patient(patient_id),
    FOREIGN KEY (patient_attribute_id) REFERENCES telenutrition.patient_attribute(patient_attribute_id),
    FOREIGN KEY (patient_fact_source_id) REFERENCES telenutrition.patient_fact_source(patient_fact_source_id),
    FOREIGN KEY (ontology_concept_id) REFERENCES telenutrition.ontology_concept(ontology_concept_id)
);

CREATE TABLE telenutrition.patient_attribute_option (     
    patient_attribute_option_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,     
    patient_attribute_id INT REFERENCES telenutrition.patient_attribute(patient_attribute_id) ON DELETE CASCADE,     
    ontology_concept_id INT REFERENCES telenutrition.ontology_concept(ontology_concept_id) ON DELETE SET NULL,
    associated_concept_id INT DEFAULT NULL REFERENCES telenutrition.ontology_concept(ontology_concept_id),
    option_code TEXT NOT NULL,
    option_text TEXT NOT NULL,  -- Links options to ontology concepts     
    UNIQUE(patient_attribute_id, ontology_concept_id, associated_concept_id)  -- Ensures unique options per attribute
);

CREATE OR REPLACE FUNCTION telenutrition.generate_fs_code() RETURNS VARCHAR AS $$
DECLARE
    max_code VARCHAR(10);
    new_number INT;
BEGIN
    -- Find the highest current custom code
    SELECT MAX(concept_code) INTO max_code 
    FROM telenutrition.ontology_concept 
    WHERE concept_code LIKE 'FS%';

    -- Extract the numeric part, increment it, and format it back as FSxxx
    IF max_code IS NULL THEN
        new_number := 1;
    ELSE
        new_number := CAST(SUBSTRING(max_code FROM 3) AS INT) + 1;
    END IF;

    RETURN 'FS' || LPAD(new_number::TEXT, 4, '0');
END;
$$ LANGUAGE plpgsql;


